import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent  implements OnInit{
  constructor() {}

  ngOnInit(): void {

  }
  registerForm= new FormGroup({
    firstname: new FormControl("",[Validators.required]),
    middlename: new FormControl(""),
    lastname: new FormControl(""),
    email: new FormControl(""),
    mobile: new FormControl(""),
    gender: new FormControl(""),
    dob: new FormControl(""),
    hsc: new FormControl(""),
    ssc: new FormControl(""),
    address: new FormControl(""),
    course: new FormControl(""),
    pwd: new FormControl(""),
    rpwd: new FormControl("")
  });

  registerSubmited(){
    console.log(this.registerForm);
  }
  get FirstName(): FormControl{
    return this.registerForm.get("firstname") as FormControl;
  } 
  get MiddleName(): FormControl{
    return this.registerForm.get("middlename") as FormControl;
  } 
  get LasttName(): FormControl{
    return this.registerForm.get("lastname") as FormControl;
  }
   get email(): FormControl{
    return this.registerForm.get("email") as FormControl;
  } 
  get mobile(): FormControl{
    return this.registerForm.get("mobile") as FormControl;
  } 
  get gender(): FormControl{
    return this.registerForm.get("gender") as FormControl;
  } 
  get dob(): FormControl{
    return this.registerForm.get("dob") as FormControl;
  }
   get hsc(): FormControl{
    return this.registerForm.get("hsc") as FormControl;
  }
   get ssc(): FormControl{
    return this.registerForm.get("ssc") as FormControl;
  }
  get address(): FormControl{
    return this.registerForm.get("address") as FormControl;
  }
  get Course(): FormControl{
    return this.registerForm.get("course") as FormControl;
  }
  get pwd(): FormControl{
    return this.registerForm.get("pwd") as FormControl;
  }
  get rpwd(): FormControl{
    return this.registerForm.get("rpwd") as FormControl;
  }
}
<form [formGroup]="registerForm" (ngSubmit)="registerSubmited()" class="border p-3 rounded-3">
    <div class="row g-0">
        <div class="col text-center pd-2 mb-3 bg-light">
<span class="fs3">Student Admission Form</span>
        </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="firtName" class="form-lable">Frist Name</label>
            <input type="text" class="form-control" id="firstname" formControlName="firstname">
            <div class="form-text text-danger" *ngIf="FirstName.errors?.['Reqired'] && FirstName.touched">
                First Name is Reqired
            </div>
        </div>
        <div class="col mb-3">
            <label for="middelname" class="form-lable">Middel Name</label>
            <input type="text" class="form-control" id="middletname" formControlName="middlename">
            <div class="form-text text-danger">
                Middle Name is Reqired
            </div>
        </div>
        <div class="col">
            <label for="lastName" class="form-lable">Last Name</label>
            <input type="text" class="form-control" id="lastname" formControlName=" lastname">
            <div class="form-text text-danger">
                Last Name is Reqired
            </div>
        </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="email" class="form-lable">Email ID</label>
            <input type="email" class="form-control" id="email" formControlName="email">
            <div class="form-text"></div>
        </div>
    </div>
    <div class="row">
        <div class="col">
            <label for="mobile" class="form-lable">Mobile Number</label>
            <input type="mobile" class="form-control" id="mobile" formControlName="mobile">
            <div class="form-text"></div>
        </div>
        <div class="col p3">
            <div class="m-1">Grnder</div>
            <div class="form-check form-check-inline">
                <input type="radio" class="form-check-input"  value="m" id="male" formControlName="gender">
                <label class="form-check-lable" for="male">Male</label>
            </div> 
            <div class="form-check form-check-inline">
                <input type="radio" class="form-check-input"  value="fm" id="female" formControlName="gender">
                <label class="form-check-lable" for="female">Female</label>
            </div> 
        </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="dob" class="form-label">Date of Birth</label>
            <input type="date" class="form-control" id="dob"  formControlName="dob">
          </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="hsc" class="form-lable">HSC Mark</label>
            <input type="mark" class="form-control" id="hsc" formControlName="hsc">
        </div>
        <div class="col mb-3">
            <label for="ssc" class="form-lable">SSC Mark</label>
            <input type="mark" class="form-control" id="ssc" formControlName="ssc">
        </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="address">Address</label>
            <input type="textarea" class="form-control" id="address" name="address" formControlName="address">
          </div>

    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="course">Course</label>
            <select class="form-control" id="course" name="course" formControlName=" course">
              <option value="bsc">Bsc</option>
              <option value="BCS">BCS</option>
              <option value="BCA">BCA</option>
              <option value="BCOM">BCOM</option>
              <option value="BBA">BBA</option>
              <!-- Add more options as needed -->
            </select>
          </div>
    </div>
    <div class="row">
        <div class="col mb-3">
            <label for="pwd" class="form-lable">Password</label>
            <input type="password" class="form-control" id="pwd" formControlName="pwd">
            <div class="form-text"></div>

        </div>
        <div class="col mb-3">
            <label for="pwd" class="form-lable">Reapt Password</label>
            <input type="password" class="form-control" id="rpwd" formControlName="rpwd">
            <div class="form-text"></div>
        </div>
    </div>
    <div class="row">
        <div class="com-10 offset-5">
            <div class="">
               <button  type="submit" class="btn btn-primary">Submit</button>
            </div>
        </div>
    </div>
</form>
